/**
 * Vendor list block
 *
 * @category    Sandy
 * @package     Sandy_Marketplace
 * @author Ultimate Module Creator
 */
class Sandy_Marketplace_Block_Vendor_List extends Mage_Core_Block_Template
{
    /**
     * initialize
     *
     * @access public
     * @author Ultimate Module Creator
     */
    public function _construct()
    {
        parent::_construct();
        $vendors = Mage::getResourceModel('sandy_marketplace/vendor_collection')
                         ->setStoreId(Mage::app()->getStore()->getId())
                         ->addAttributeToSelect('*')
                         ->addAttributeToFilter('status', 1);
        $vendors->setOrder('shopurl', 'asc');
        $this->setVendors($vendors);
    }

    /**
     * prepare the layout
     *
     * @access protected
     * @return Sandy_Marketplace_Block_Vendor_List
     * @author Ultimate Module Creator
     */
    protected function _prepareLayout()
    {
        parent::_prepareLayout();
        $pager = $this->getLayout()->createBlock(
            'page/html_pager',
            'sandy_marketplace.vendor.html.pager'
        )
        ->setCollection($this->getVendors());
        $this->setChild('pager', $pager);
        $this->getVendors()->load();
        return $this;
    }

    /**
     * get the pager html
     *
     * @access public
     * @return string
     * @author Ultimate Module Creator
     */
    public function getPagerHtml()
    {
        return $this->getChildHtml('pager');
    }
}
